import Header from '../components/Header';
import Link from 'next/link'
import { getArticleList } from '../lib/api/article';
import Card from '../components/Card';

// const cards = props => {

//         props.articles.map((ele,index) => 
//         <Card info={ele} key={index}/>
//         );
// }

const Articles = props => {
    return (
        <div style={{ padding: '10px 45px' }}>
        <Header/>
        <h1>Article List</h1>
            { <ul>
                {props.articles.map(article =>(
                  <li key={article._id}>
                   <Link href="/detail/[_id]/[slug]" as={`/detail/${article._id}/${article.slug}`}>
                         <a>{article.title}</a>
                 </Link>
                  </li>   
                ))}
            </ul>}
            {/* {props.articles.map((article,index) =>(
                <Card info={article} key={index}/>
            ))} */}
        </div>
    )
}

Articles.getInitialProps = async function(){
      const res = await getArticleList();
      return res;
}

export default Articles;
