import Header from  '../../../components/Header';
import Layout from '../../../components/MyLayout';
import { getArticleDetail } from '../../../lib/api/article';
import dynamic  from 'next/dynamic';
import marked from 'marked'

const Highlight = dynamic(() => import('react-highlight'));

const Post = props => {
          console.log(props)
    if(/~~~/.test(props.content)) {
        return (
          <div style={{ padding: '10px 45px' }}>
            <Header/>
            <h1>{props.title}</h1>
            <Highlight innerHTML>{marked(props.content)}</Highlight>
          </div>
        );


      }
  return  (
    <div style={{ padding: '10px 45px' }}>
    <Header/>
     <h1>{props.title}</h1>
     <div dangerouslySetInnerHTML={{ __html: marked(props.content) }} />
    </div>)
}


Post.getInitialProps = async function(context) {
    const {_id , slug} = context.query;
    const res = await getArticleDetail({_id,slug})
    const article = res.article;
    return {article};
}
export default Post;
