import sendRequest from "./sendRequest";

const BASE_PATH = '/api/v1';

export const addArticle = ({title,slug,content}) => 
    sendRequest(`${BASE_PATH}/articles/add`, {
        body: JSON.stringify({title,slug,content}),
    });
