import React from 'react';
import Snackbar from '@material-ui/core/Snackbar';

let openSnackbarFn = (message) => {console.log(message);return message;};

export default function WithNotifyMessage(options) {
class Notifier extends React.Component {
  state = {
    open: false,
    message: '',
    vertical: 'top',
    horizontal: 'center'
  };

  componentDidMount() {
   // openSnackbarFn = this.openSnackbar;
    //console.log('Component Did Mount',openSnackbarFn);
    this.setState({open: options.open , message: options.message});
  }

  handleSnackbarRequestClose = () => {
    this.setState({
      open: false,
      message: '',
    });
  };

  openSnackbar = ({ message }) => {
    this.setState({ open: true, message });
  };

  render() {
    const message = (
      <span id="snackbar-message-id" dangerouslySetInnerHTML={{ __html: this.state.message }} />
    );

    return (
      <Snackbar
        anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
        message={message}
        autoHideDuration={5000}
        onClose={this.handleSnackbarRequestClose}
        open={this.state.open}
        ContentProps={{
          'aria-describedby': 'snackbar-message-id',
        }}
      />
    );
  }
}
}
