import Header from '../components/Header';
import Link from 'next/link';
import { getArticleList } from '../lib/api/article';
import MyCard from '../components/MyCard';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

// const cards = props => {

//         props.articles.map((ele,index) => 
//         <Card info={ele} key={index}/>
//         );
// }

const useStyles = makeStyles(theme => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      height: 100,
      width: 100,
    },
    control: {
      padding: theme.spacing(2),
    },
  }));

const Articles = list => {

   const classes = useStyles();
   const [spacing, setSpacing] = React.useState(2);

    return (
        <div>
        <Header/>
        <h1>Article List</h1>
            {/* { <ul>
                {props.articles.map(article =>(
                  <li key={article._id}>
                   <Link href="/detail/[_id]/[slug]" as={`/detail/${article._id}/${article.slug}`}>
                         <a>{article.title}</a>
                 </Link>
                  </li>   
                ))}
            </ul>} */}
            <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12} sm={6} md={3}>
            {list.articles.map((article,index) =>(
                <MyCard info={article} key={article._id}/>
            ))}
            </Grid>
            </Grid>
        </div>
    )
}

Articles.getInitialProps = async () =>{
      const res = await getArticleList();
      return {
        articles: res.articles.map(entry => entry.articles)
      };
}

export default Articles;
