import React from 'react';
import TextField from '@material-ui/core/TextField';
import PropTypes from 'prop-types';
import {styleBody,styleTextField,styleTextArea}  from '../components/SharedStyles';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import Button from '@material-ui/core/Button';
import EditArticle from '../components/EditArticle';
import { addArticle } from '../lib/api/article';
import { useRouter } from 'next/router';

class AddArticle extends React.Component {

    addArticleOnSave = async (data) => {
        const router = useRouter();
        try {
            const article = await addArticle(data);
            router.push(`/detail/[id]/[slug]`, `/detail/${article.id}/${article.slug}`);

        }catch(err){
            console.log(err);
        }

    }


    render() {
        return (
            <div style={styleBody}>
              <EditArticle onSave={this.addArticleOnSave}/>
            </div>
        )
    }
}

export default AddArticle;
