import React ,{useState, useEffect,Fragment} from 'react';


const Paignator = ({skip, range, handlePaginationChange}) => {

    skip = !!skip? skip : 0;

    const [page, setPageNumber] = useState(1);

    useEffect(() => {
        return setPageNumber(skip);
    },
    [skip]);

    const moveToNextPage = () => {

        if(page > 1) {
            handlePaginationChange(page-1);
            return setPageNumber(page-1);
        }

        return null;
    }

    const moveToPreviousPage = () => {
        if(page < range[range.length-1]) {
            handlePaginationChange(page+1);
            return setPageNumber(page+1);
        }
        return null;
    }

   const  moveToPage = (pageNumber) => {
        handlePaginationChange(pageNumber);
        return setPageNumber(pageNumber);
    }

    const renderPageIndicators = ({num, index}) => {
        return (
            <span onClick={() => moveToPage(num)}>
                {num}
            </span>
            // <span>{num}</span>
        )
    }

    return (
            <Fragment>
            <div>
                {range.length > 1 ?(
                <button onClick={moveToNextPage}>
                    <span>{'<'}</span>
                    <span>Previous</span>
                </button>):null}
             {range.map(renderPageIndicators)}

             {range.length > 1 ? (
                 <button onClick={moveToPreviousPage}>
                     <span>Next</span>{' '}
                     <span>{'>'}</span>
                 </button>
             ):null}
            </div>
            </Fragment>

    );
};

export default Paignator;
