import React from 'react';
import TextField from '@material-ui/core/TextField';
import PropTypes from 'prop-types';
import {styleBody,styleTextField}  from '../components/SharedStyles';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';

class AddArticle extends React.Component {

  static  propTypes = {
        title: PropTypes.string.isRequired,
        content: PropTypes.string.isRequired,
        slug: PropTypes.string.isRequired
    };

    constructor(props) {
        super(props);

        this.state = {
            title: props.title || '',
            slug: props.slug || '' ,
            content: props.content || ''
        }
    }



    render() {
        return (
            <div style={styleBody}>
              <form onSubmit={this.onSubmit}>
                <TextField id="title" style={styleTextField}
                   onChange={(event) => {
                       this.setState({title: event.target.value})
                   }}
                value={this.state.title}
                type="text"
                label="Article's title"
                required
                />
                <br/>
                <TextField id="slug" style={styleTextField}
                   onChange={(event) => {
                       this.setState({title: event.target.value})
                   }}
                value={this.state.slug}
                type="text"
                label="Article's slug"
                required
                />
                <br/>
                <TextareaAutosize
                    style={styleTextField}
                    rowsMax={10}
                    aria-label="Article's Content"
                    placeholder="Article's Content"
                />  
                </form>
            </div>
        )
    }
}

export default AddArticle;
