import React from 'react';
import TextField from '@material-ui/core/TextField';
import PropTypes from 'prop-types';
import {styleBody,styleTextField,styleTextArea}  from '../components/SharedStyles';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import Button from '@material-ui/core/Button';

class EditArticle extends React.Component {

  static  propTypes = {
    title: PropTypes.string.isRequired,
    content: PropTypes.string.isRequired,
    slug: PropTypes.string.isRequired,
    onSave: PropTypes.func.isRequired,

};

static defaultProps = {
    title:'',
    slug:'',
    content:'',
    errors: {
        title : '',
        slug : '',
        content: ''
    },
    haveErrored: false

}

constructor(props) {
    super(props);

    this.state = {
        title: props.title || '',
        slug: props.slug || '' ,
        content: props.content || '',
        errors: props.errors,
        haveErrored: props.haveErrored
    }
}

onSubmit = (event) => {
    event.preventDefault();
    let {title , slug , content} = this.state;

    if (!title) {
        //notify('Title is required');
        this.setState({haveErrored:true ,errors:{title:'Title is required'}});
        console.log(this.state);
        return;
      }

      if (!slug) {
        //notify('Slug is required');
        return;
      }

      if (!content) {
        notify('Content repo is required');
        return;
      }
    this.props.onSave(this.state);
}

render() {
    return (

          <form onSubmit={this.onSubmit}>
          <TextField id="title" style={styleTextField}
               onChange={(event) => {
                   this.setState({title: event.target.value})
               }}
            value={this.state.title}
            type="text"
            label="Article's title"
            required
            error={this.state.haveErrored}
            helperText={this.state.haveErrored ? this.state.errors.title : ''}

            />
            <br/>
            <TextField id="slug" style={styleTextField}
               onChange={(event) => {
                   this.setState({slug: event.target.value})
               }}
            value={this.state.slug}
            type="text"
            label="Article's slug"
            required

            />
            <br/>
            <br/>
            <TextareaAutosize
                style={styleTextArea}
                rowsMax={4}
                value={this.state.content}
                onChange={(event) => {
                    this.setState({content: event.target.value})
                }}
                placeholder="Article's content"
            />  
            <br/>
            <Button variant="contained" type="submit" color="primary">
                Save
             </Button>
          </form>

    )
}
}

export default EditArticle;
