import Header from '../components/Header';
import Link from 'next/link';
import { getArticleList } from '../lib/api/article';
import MyCard from '../components/MyCard';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paginator from '../components/Pagination';
import { useState } from 'react';

// const cards = props => {

//         props.articles.map((ele,index) => 
//         <Card info={ele} key={index}/>
//         );
// }
const calculateRange = length => Array.from({ length }, (v, k) => k + 1);

const Articles = list => {

    const [limit, updateLimit] = useState(10);
    const totalPage = list.articles.length / limit;
    const [page , updatePage] = useState(!!totalPage ? totalPage : 1);
    const rangeLimit = Math.ceil(list.article > 0 ? list.articles.length/ limit : 0);
    const range = calculateRange(rangeLimit);

    console.log('Range !!!!!!!!!!', range)
    return (
        <div>
        <Header/>
        <h1>Article List</h1>
            {/* { <ul>
                {props.articles.map(article =>(
                  <li key={article._id}>
                   <Link href="/detail/[_id]/[slug]" as={`/detail/${article._id}/${article.slug}`}>
                         <a>{article.title}</a>
                 </Link>
                  </li>   
                ))}
            </ul>} */}

            {list.articles.map((article,index) =>(
                <MyCard info={article} key={article._id}/>
            ))}

            <Paginator
                 handlePaginationChange={event => updatePage(event)}
                 range={range}
                 skip={page}
             />

        </div>
    )
}

Articles.getInitialProps = async () =>{
      const res = await getArticleList();
      return {
        articles: res.articles.map(entry => entry.articles)
      };
}

export default Articles;
